import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faCartPlus } from '@fortawesome/free-solid-svg-icons';

const ProductList = ({ onAddToCart }) => {
    const [products, setProducts] = useState([]);
    const [selectedCategory, setSelectedCategory] = useState(null);
    const [cart, setCart] = useState([]);

    useEffect(() => {
      let url = 'https://fakestoreapi.com/products';
      if (selectedCategory) {
        url += `/category/${selectedCategory}`;
      }
      axios.get(url)
        .then(response => {
          setProducts(response.data);
        })
        .catch(error => {
          console.log(error);
        });
    }, [selectedCategory]);

    const handleAddToCart = (productId) => {
      const product = products.find(p => p.id === productId);
      if (product) {
        setCart([...cart, product]);
        onAddToCart(product);
      }
    }

    const handleCategorySelect = (category) => {
      setSelectedCategory(category);
    }

    return (
      <section className="section">
        <div className="container">
          <div className="buttons has-addons">
            <button className={`button ${selectedCategory === null ? 'is-primary is-selected' : ''}`} onClick={() => handleCategorySelect(null)}>All</button>
            <button className={`button ${selectedCategory === 'men clothing' ? 'is-selected' : ''}`} onClick={() => handleCategorySelect('men\'s%20clothing')}>Men's Clothing</button>
            <button className={`button ${selectedCategory === 'jewelery' ? 'is-selected' : ''}`} onClick={() => handleCategorySelect('jewelery')}>Jewelery</button>
            <button className={`button ${selectedCategory === 'electronics' ? 'is-selected' : ''}`} onClick={() => handleCategorySelect('electronics')}>Electronics</button>
            <button className={`button ${selectedCategory === 'women clothing' ? 'is-selected' : ''}`} onClick={() => handleCategorySelect('women\'s%20clothing')}>Women's Clothing</button>
          </div>
          <div className="columns is-multiline">
            {products.map(product => (
              <div key={product.id} className="column is-one-third">
                <div className="card has-shadow">
                  <div className="card-image">
                    <figure className="image is-4by3">
                      <img src={product.image} alt={product.title.substring(0, 25)} className="product-image" />
                    </figure>
                  </div>
                  <div className="card-content">
                    <p className="title is-4">{product.title.substring(0, 25)}</p>
                    <p className="subtitle is-6">${product.price}</p>
                    <button
                      className="button is-primary"
                      onClick={() => handleAddToCart(product.id)}
                    >
                      <span>Add to Cart</span>
                      <span className="icon">
                        <FontAwesomeIcon icon={faCartPlus} />
                      </span>
                    </button>
                  </div>
                </div>
              </div>
            ))}
          </div>
        </div>
      <style jsx>{`
        .card {
            border-radius: 8px;
            height: 500px;
        }

        .has-shadow {
          box-shadow: 0 0 10px rgba(0, 0, 0, 0.1);
        }

        .button {
          display: flex;
          align-items: center;
          justify-content: center;
        }

        .button span:not(:last-child) {
          margin-right: 0.5rem;
        }
      `}</style>
    </section>
  );
}

export default ProductList;
